import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [resultUrls, setResultUrls] = useState([]);
  const [error, setError] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(null);
  const [eventSource, setEventSource] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select an image file.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);

    try {
      // Open EventSource to get elapsed time updates
      const es = new EventSource('http://localhost:3000/upload');
      setEventSource(es);

      es.onmessage = (event) => {
        console.log('Received elapsed time:', event.data);
        setElapsedTime(event.data);
      };

      // Send image file to backend for processing
      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Assuming backend returns an array of URLs in response.data
      setResultUrls(response.data);
      setError(null);
    } catch (error) {
      console.error('Error uploading image:', error);
      setError('An error occurred while uploading the image.');
      setResultUrls([]);
    }
  };

  const handleCloseEventSource = () => {
    if (eventSource) {
      eventSource.close();
      setEventSource(null);
    }
  };

  return (
    <div className="App">
      <h1>Image Uploader</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} accept="image/*" />
        <button type="submit">Upload</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {elapsedTime !== null && (
        <p>Time passed since upload: {elapsedTime} seconds</p>
      )}
      {resultUrls.length > 0 && (
        <div>
          <h2>Results:</h2>
          {resultUrls.map((url, index) => (
            <div key={index}>
              {url.endsWith('.png') || url.endsWith('.jpg') || url.endsWith('.jpeg') ? (
                <img src={url} alt={`Processed Image ${index}`} style={{ maxWidth: '100%', height: 'auto' }} />
              ) : (
                <video controls style={{ maxWidth: '100%', height: 'auto' }}>
                  <source src={url} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              )}
              <br />
              <a href={url} target="_blank" rel="noopener noreferrer">Download Result {index + 1}</a>
            </div>
          ))}
          <button onClick={handleCloseEventSource}>Close Event Source</button>
        </div>
      )}
    </div>
  );
}

export default App;
