import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select an image file.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);

    try {
      const eventSource = new EventSource('http://localhost:3000/upload');
      eventSource.onmessage = (event) => {
        setElapsedTime(event.data);
      };

      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setResult(response.data[0]);
      setError(null);
      eventSource.close(); // Close EventSource after processing completes
    } catch (error) {
      console.error('Error uploading image:', error);
      setError('An error occurred while uploading the image.');
      setResult(null);
    }
  };

  return (
    <div className="App">
      <h1>Image Uploader</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} accept="image/*" />
        <button type="submit">Upload</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {elapsedTime && (
        <p>Time passed since upload: {elapsedTime} seconds</p>
      )}
      {result && (
        <div>
          <h2>Result:</h2>
          <img src={result} alt="Processed Result" style={{ maxWidth: '100%', height: 'auto' }} />
        </div>
      )}
    </div>
  );
}

export default App;
